// Simple if statement.

package sankha_java;

public class simple_if {

	public static void main(String[] args) {    // main method
		// TODO Auto-generated method stub
		int x = 5;   // declare x = 5
		int y = 3;   // declare y = 3
		if(x+y<10) {    // condition
			System.out.println("x + y is less than 10.");   // print the output
		}

	}

}
